mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY TO_NUMBER(JOIN_YMD)
DESC
ASC
) AS RNUM , C.COM_NM AS RGN_NM , M.MBR_ID -- 회원 아이디 , M.MBR_PSWD -- 회원 비밀번호 , M.MBR_NM -- 회원 이름 , M.MBR_ZIP -- 우편번호 , M.MBR_ADDR -- 주소 , M.MBR_DADDR -- 상세주소 , M.MBR_TELNO -- 전화번호 , M.MBR_BRDT -- 생년월일 , M.MBR_EML_ADDR -- 이메일 주소 , M.ENABLED -- 권한 사용 여부 , M.DEL_YN -- 탈퇴 여부 , M.JOIN_YMD -- 가입 일자 , M.MBR_TYPE -- 회원 유형 , M.MBR_APRV_YN -- 회원승인여부 , M.MBR_IMG_PATH -- 회원 이미지 경로 , M.RGN_NO -- 지역 번호 FROM MEMBER M LEFT OUTER JOIN COM_CODE C ON C.COM_NO = M.RGN_NO WHERE M.MBR_TYPE = 'MBR01' -- 회원 유형 WHERE조건
AND M.MBR_NM LIKE '%' || #{mbrNm} || '%'
AND M.RGN_NO = #{rgnNo}
AND M.DEL_YN = #{delYn}
AND TO_DATE(M.JOIN_YMD, 'YYYY-MM-DD') >= #{bgngYmd}
AND TO_DATE(M.JOIN_YMD, 'YYYY-MM-DD') <= #{expYmd}
) WHERE RNUM BETWEEN (#{currentPage} * 10) - (10 - 1) AND (#{currentPage} * 10)

SELECT COUNT(*)
FROM   MEMBER
WHERE  MBR_TYPE = 'MBR01'


	AND MBR_NM LIKE '%' || #{mbrNm} || '%'



	AND RGN_NO = #{rgnNo}



	AND DEL_YN  = #{delYn}




	AND TO_DATE(JOIN_YMD, 'YYYY-MM-DD') >= #{bgngYmd}





	AND TO_DATE(JOIN_YMD, 'YYYY-MM-DD') <= #{expYmd}
