mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY TO_NUMBER(Q.QS_SEQ) DESC) AS RNUM , C.COM_NM AS QS_TYPE_NM , Q.QS_SEQ , Q.QS_TTL , Q.QS_CN , MEM.MBR_NM , MEM.MBR_ID , Q.WRTR_DT , MN.MNGR_ID , MNGR.MBR_NM AS MNGR_NM , Q.QS_TYPE , FD.FILE_SN , FD.FILE_GROUP_NO , FD.FILE_ORIGINAL_NAME , FD.FILE_SAVE_NAME , FD.FILE_SAVE_LOCATE , FD.FILE_SIZE , FD.FILE_EXT , FD.FILE_MIME , FD.FILE_FANCYSIZE , FD.FILE_SAVE_DATE , FD.FILE_DOWNCOUNT FROM QS Q LEFT OUTER JOIN MNGR MN ON Q.MNGR_ID = MN.MNGR_ID LEFT OUTER JOIN MEMBER MEM ON Q.MBR_ID = MEM.MBR_ID LEFT OUTER JOIN MEMBER MNGR ON MN.MNGR_ID = MNGR.MBR_ID LEFT OUTER JOIN COM_CODE C ON C.COM_NO = Q.QS_TYPE LEFT OUTER JOIN FILE_GROUP FG ON Q.FILE_GROUP_NO = FG.FILE_GROUP_NO LEFT OUTER JOIN FILE_DETAIL FD ON FG.FILE_GROUP_NO = FD.FILE_GROUP_NO WHERE 1=1
AND Q.QS_TYPE = #{qsType}
AND Q.QS_TTL LIKE '%' || #{qsTtl} || '%'
AND TO_CHAR(WRTR_DT,'YYYY-MM-DD') >= #{bgngYmd}
AND TO_CHAR(WRTR_DT,'YYYY-MM-DD') <= #{expYmd}
) WHERE RNUM BETWEEN (#{currentPage} * 10) - (10 - 1) AND (#{currentPage} * 10)

SELECT COUNT(*) 
FROM   QS
WHERE  1=1

	AND QS_TYPE = #{qsType}



	AND QS_TTL LIKE '%' || #{qsTtl} || '%'








	AND TO_CHAR(WRTR_DT,'YYYY-MM-DD') >= #{bgngYmd}





	AND TO_CHAR(WRTR_DT,'YYYY-MM-DD') <= #{expYmd}

mapper.update:
	UPDATE QS
    SET MNGR_ID = #{mngrId}, 
        ANS_DT = SYSDATE, 
        ANS_CN = #{ansCn}
    WHERE QS_SEQ = #{qsSeq}
